mapper.select:
select user_id  from
customer_account ca
WHERE ca.auditor_id = #{auditor_id}
AND  ca.open_date
BETWEEN #{start}  AND #{end};

SET AUTOCOMMIT = 0;
BEGIN
	WORK;
SELECT DISTINCT @a:=
u.user_id AS id
FROM
account_info ai,
user u
WHERE
ai.security_id = #{security_id}
AND u.STATUS = 4
LIMIT 1 FOR UPDATE;
UPDATE user u
SET  u.status=5
#SELECT * FROM user u
WHERE u.user_id=@a;
SELECT @a;
COMMIT WORK;

select am.security_id security_id
FROM auditor_manager am
WHERE am.auditor_id=#{auditor_id}

mapper.update:
update auditor_manager
set
security_id=#{security_id}
where auditor_id=#{auditor_id}
