mapper.select:
 SELECT
 count(*)
 FROM
user u
 WHERE u.phone = #{phone}
 AND u.password=#{password}

mapper.update:
UPDATE user u

    u.status=#{status}

WHERE  u.user_id=#{user_id};

mapper.select:
    select u.user_id user_id
    from user u,account_info ac
WHERE  ac.security_id=#{security_id}
And u.user_id=ac.user_id
AND ac.name=#{name}
